mapper.select:
select a.order_id orderId, a.order_status orderStatus, a.order_money orderMoney, a.goods_cnt goodsCnt, a.order_number orderNumber, a.version from t_order_master a where a.is_delete = 0
and a.order_status = #{orderStatus}
and a.customer_id = #{customerId}
and a.shop_id = #{shopId}
and (a.order_number like concat('%',#{keyword},'%') or EXISTS (select 1 from t_order_commodity c left join t_commodity b on b.commodity_id = c.commodity_id where c.commodity_id in a.commodity_Id and c.order_id = a.order_id and c.commodity_name like concat('%',#{keyword},'%')))
order by a.create_time desc

select b.commodity_id commodityId, b.commodity_name commodityName, b.introduce, a.commodity_totalpri as commodityTotalpri, a.commodity_num as commodityNum, (select f.path from t_file f where f.biz_id = a.commodity_id order by f.create_time asc limit 1) as path from t_order_commodity a left join t_commodity b on b.commodity_id = a.commodity_id where b.is_delete = 0
and a.order_id = #{orderId}
order by a.create_time desc
